// Your first JavaScript program
console.log('Hello, World!');

// Variables in JavaScript
let message = 'Welcome to JavaScript!';
console.log(message);

// Basic math operations
let x = 10;
let y = 5;
console.log('Sum:', x + y);
console.log('Product:', x * y);

// Variable declarations
let name = 'Alice';           // String
let age = 25;                 // Number  
let isStudent = true;         // Boolean
let address;                  // Undefined
let middleName = null;        // Null

// Constants cannot be reassigned
const PI = 3.14159;
const GREETING = 'Hello!';

// Check data types
console.log(typeof name);      // "string"
console.log(typeof age);       // "number"
console.log(typeof isStudent); // "boolean"
console.log(typeof address);   // "undefined"

// Variable reassignment
name = 'Bob';  // This works with let
age = 26;      // This also works

console.log(name, age);

// Working with Numbers
let price = 29.99;
let quantity = 3;
let total = price * quantity;

console.log('Total:', total);  // 89.97

// Math operations
let radius = 5;
let area = Math.PI * radius ** 2;
console.log('Circle area:', Math.round(area));

// Random numbers
let randomNum = Math.random();  // 0 to 1
let dice = Math.floor(Math.random() * 6) + 1;  // 1 to 6
console.log('Dice roll:', dice);

// Working with Strings
let firstName = 'John';
let lastName = 'Doe';
let fullName = firstName + ' ' + lastName;  // Concatenation

// Template literals (modern way)
let greeting = `Hello, ${firstName}! You are ${age} years old.`;
console.log(greeting);

// String methods
console.log(fullName.length);        // 8
console.log(fullName.toUpperCase()); // "JOHN DOE"
console.log(fullName.slice(0, 4));   // "John"

